package com.r0adkll.deckbuilder.arch.data.features.preview

import com.google.gson.Gson
import com.jakewharton.rxrelay2.BehaviorRelay
import com.r0adkll.deckbuilder.arch.domain.features.remote.model.ExpansionPreview
import com.r0adkll.deckbuilder.arch.domain.features.preview.PreviewRepository
import io.reactivex.Observable
import java.io.IOException


class TestPreviewRepository : PreviewRepository {

    private val gson = Gson()
    private val testingPreviewJson = "{\"version\":2,\"expiresAt\":\"2019-03-01T12:00Z\",\"code\":\"sm9\",\"preview\":{\"logo\":{\"url\":\"https://images.pokemontcg.io/sm9/logo.png\",\"margins\":{\"top\":16}},\"title\":\"Team Up is here!\",\"description\":\"Over 180 cards! \\n6 brand-new TAG TEAM Pokémon-GX\\n6 more Pokémon-GX\\n4 Prism Star cards\\nMore than 25 Trainer cards\",\"textColor\":\"#FFFFFF\",\"background\":[{\"source\":{\"type\":\"base64\",\"value\":\"\",\"density\":2},\"tile\":{\"x\":\"repeat\",\"y\":\"repeat\"}}],\"foreground\":{\"source\":{\"type\":\"url\",\"value\":\"https://firebasestorage.googleapis.com/v0/b/deck-builder-1b711.appspot.com/o/sm9_foreground_2.png?alt=media&token=f980f76c-f421-4abc-88be-cf1652c35025\"},\"aspectRatio\":true}}}"
    private var dismissed = BehaviorRelay.createDefault(false)


    override fun getExpansionPreview(): Observable<ExpansionPreview> {
        return dismissed.switchMap {
            if (!it) {
                val preview = gson.fromJson(testingPreviewJson, ExpansionPreview::class.java)
                Observable.just(preview)
            } else {
                Observable.error(IOException("No preview found"))
            }
        }
    }

    override fun dismissPreview() {
        dismissed.accept(true)
    }
}